'use strict';
/*!
 * ignore
 */

module.exports = function combinePathErrors(err) {
  var keys = Object.keys(err.errors || {});
  var len = keys.length;
  var msgs = [];
  var key;

  for (var i = 0; i < len; ++i) {
    key = keys[i];

    if (err === err.errors[key]) {
      continue;
    }

    msgs.push(key + ': ' + err.errors[key].message);
  }

  return msgs.join(', ');
};