'use strict';

var dotRE = /\./g;

module.exports = function parentPaths(path) {
  if (path.indexOf('.') === -1) {
    return [path];
  }

  var pieces = path.split(dotRE);
  var len = pieces.length;
  var ret = new Array(len);
  var cur = '';

  for (var i = 0; i < len; ++i) {
    cur += cur.length !== 0 ? '.' + pieces[i] : pieces[i];
    ret[i] = cur;
  }

  return ret;
};